import java.util.*;public class NextRoundSel {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int k = sc.nextInt();        int[] con = new int[n];        for(int i = 0; i < n; i ++) {            con[i] = sc.nextInt();        }        int score = con[k - 1];        int c = 0;        for(int i = 0; i < n; i ++) {            if(con[i] == 0) {                continue;            }            if(con[i] >= score) {                c ++;            }        }        System.out.println(c);    }}