import java.util.*;public class LineTrip {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t -- > 0) {            int n = sc.nextInt();            int x = sc.nextInt();            int[] fuel = new int[n];            for(int i = 0; i < n; i ++) {                fuel[i] = sc.nextInt();            }            System.out.println(CalculateFuel(n, x, fuel));        }    }    public static int CalculateFuel(int n, int x, int[] fuel) {         int ans = fuel[0];        for(int i = 1; i < n; i ++) {            ans = Math.max(ans, fuel[i] - fuel[i-1]);        }        ans = Math.max(ans, 2*(x - fuel[n-1]));        return ans;    }}