import java.io.*;import java.util.*;public class CutArrayA{ public static void main(String[] args) throws Exception{  BufferedReader br=new BufferedReader(new InputStreamReader(System.in));  int t=Integer.parseInt(br.readLine().trim());  StringBuilder sb=new StringBuilder();  while(t-->0){   int n=Integer.parseInt(br.readLine().trim());   StringTokenizer st=new StringTokenizer(br.readLine());   int[] a=new int[n+1];   for(int i=1;i<=n;i++) a[i]=Integer.parseInt(st.nextToken());   int[] p=new int[n+1];   for(int i=1;i<=n;i++) p[i]=(p[i-1]+a[i])%3;   boolean ok=false;   for(int l=1;l<=n-2 && !ok;l++){    for(int r=l+1;r<=n-1 && !ok;r++){     int s1=p[l];     int s2=(p[r]-p[l]+3)%3;     int s3=(p[n]-p[r]+3)%3;     if((s1==s2 && s2==s3) || (s1!=s2 && s1!=s3 && s2!=s3)){      sb.append(l).append(' ').append(r).append('\n');      ok=true;     }    }   }   if(!ok) sb.append("0 0\n");  }  System.out.print(sb.toString()); }}