import java.util.*;public class Main{    public static void main(String[] args)    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t > 0)        {            int x = sc.nextInt();            int n = sc.nextInt();            System.out.println(CalculateSum(x, n));            t --;        }        sc.close();    }    public static int CalculateSum(int x, int n)    {        int[] arr = new int[n];        for (int i = 0; i < n; i ++)        {            arr[i] = x;            x *= -1;        }        int sum = 0;        for (int i = 0; i < n; i ++)        {            sum += arr[i];        }        return sum;    }}