import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        for (int test = 0; test < t; test++) {            int n = sc.nextInt();            int m = sc.nextInt();            int x = sc.nextInt();            int y = sc.nextInt();            int[] a = new int[n];            int[] b = new int[m];            for (int i = 0; i < n; i++) a[i] = sc.nextInt();            for (int i = 0; i < m; i++) b[i] = sc.nextInt();            int h = lowerBound(a, y);            int v = lowerBound(b, x);            System.out.println(h + v);        }    }    static int lowerBound(int[] arr, int target) {        int l = 0, r = arr.length;        while (l < r) {            int mid = (l + r) / 2;            if (arr[mid] < target) l = mid + 1;            else r = mid;        }        return l;    }}