import java.util.*;public class Main {    public static void main(String[] args) {        Scanner z = new Scanner(System.in);        int y = z.nextInt();        for (int a = 0; a < y; a++) {            long p = z.nextLong(), q = 0;            while (p > 2) {                long u = p / 3, v = p / 3, w = p - u - v;                if (u < 1) u = 1;                if (w < v) {                    long tmp = v;                    v = w;                    w = tmp;                }                q += u;                p = w;            }            System.out.println(q);        }    }}