import java.util.*;public class BitPlay {    static int x = 0;    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        while(n -- > 0) {            String s = sc.next();            ansGame(s);        }        System.out.println(x);    }    public static void ansGame(String s) {        if(s.charAt(1) == '+') {            x += 1;        }        else {            x -= 1;        }    }}